// Returns 5 rows as proper n8n items with "json" wrapper
// Field names match your Airtable: Team, Update, KPI's, Links

const rows = [
  {
    Team: "Engineering",
    Update: "Shipped API v2.3; reduced p95 latency.",
    Kpis: "p95_latency_ms: 380->312; error_rate: -22%",
    Links: "https://docs.example.com/release-v2-3"
  },
  {
    Team: "Sales",
    Update: "Closed 8 mid-market deals; pipeline healthy.",
    Kpis: "deals_closed: 8; pipeline_value_inr: 1.8cr; win_rate: 32%",
    Links: "https://crm.example.com/opps?week=latest"
  },
  {
    Team: "Marketing",
    Update: "Launched UGC campaign; best CTR this quarter.",
    Kpis: "impressions: 410000; clicks: 18950; ctr: 4.6%; cac_inr: 142",
    Links: "https://campaigns.example.com/ugc-q3"
  },
  {
    Team: "Operations",
    Update: "Cut cloud cost with rightsizing & spot usage.",
    Kpis: "cloud_cost_inr: -18%; incidents: 0; uptime: 99.97%",
    Links: "https://runbooks.example.com/cost-rightsizing"
  },
  {
    Team: "Product",
    Update: "Completed usability tests; two P0 fixes shipped.",
    Kpis: "nps: 42; csat: 4.6/5; tasks_closed: 27",
    Links: "https://product.example.com/research/usability-week"
  }
];

// Map to n8n items and use the exact Airtable field key "KPI's"
return rows.map(r => ({
  json: {
    Team: r.Team,
    Update: r.Update,
    "KPI's": r.Kpis,
    Links: r.Links
  }
}));
