// Robustly parse the agent's single-string output:
//
// Expected shape (exactly as your agent produces):
// REPLY:
// <plain text>
//
// SUBJECT:
// <plain text>
//
// Also tolerates an optional first JSON line like:
// {"needs_human": true, "category":"support","confidence":0.72}

const raw =
  $json.output                                     // your Agent's field
  || $json.text
  || $json.data
  || $json.choices?.[0]?.message?.content
  || $json.choices?.[0]?.text
  || '';

let body = String(raw || '').trim();
const result = {
  replyText: '',
  replySubject: '',
  needs_human: false,
  raw
};

// Optional first-line JSON (escalation flag)
const firstLine = body.split('\n',1)[0].trim();
if (firstLine.startsWith('{') && firstLine.endsWith('}')) {
  try {
    const meta = JSON.parse(firstLine);
    if (typeof meta.needs_human === 'boolean') result.needs_human = meta.needs_human;
    body = body.slice(firstLine.length).trim();
  } catch {}
}

// Extract REPLY + SUBJECT blocks
const m = body.match(/REPLY:\s*([\s\S]*?)\n+SUBJECT:\s*([\s\S]*)$/i);
if (m) {
  result.replyText = m[1].trim();
  result.replySubject = m[2].trim();
} else {
  // Fallback: treat whole text as reply; subject defaults from earlier nodes
  result.replyText = body || 'Thanks for your email ‚Äî could you please share a few more details so I can help?';
  result.replySubject = '';
}

// Provide a safe default subject from upstream if agent did not supply one
const upstreamSubject =
  $json['Subject '] || $json.Subject || $json.subject || 'Regarding your request';
if (!result.replySubject) {
  result.replySubject = `Re: ${upstreamSubject}`.trim();
}

return [{ json: result }];
